import{_ as a,c as i,a as t,o as s}from"./index-DwiJetLm.js";const n={class:"about-page"},l={__name:"AboutView",setup(d){return(c,e)=>(s(),i("main",n,e[0]||(e[0]=[t('<section class="section project-goals" data-v-ed4c44b9><div class="responsive-container" data-v-ed4c44b9><h2 class="section-title" data-v-ed4c44b9>PROJECT GOALS</h2><div class="content-box" data-v-ed4c44b9><p class="goals-summary" data-v-ed4c44b9> The GDCAU – Green Deal for Central Asian Universities project aims to empower universities in Central Asia as key drivers of sustainable development, aligning them with the European Green Deal framework. </p><p class="goals-summary" data-v-ed4c44b9> The project focuses on developing environmentally sustainable education programs, strengthening university governance, and expanding cooperation between universities, businesses, and society. </p></div></div></section><section class="section strategic-objectives" data-v-ed4c44b9><div class="responsive-container" data-v-ed4c44b9><h2 class="section-title" data-v-ed4c44b9>STRATEGIC OBJECTIVES</h2><ol class="objectives-list" data-v-ed4c44b9><li class="objective-item" data-v-ed4c44b9> Strengthening institutional capacity for sustainable development strategies <ul data-v-ed4c44b9><li data-v-ed4c44b9>Develop new management strategies focused on ESG transformation.</li><li data-v-ed4c44b9>Update regulations to incorporate sustainability principles.</li><li data-v-ed4c44b9>Train leadership in strategic planning related to the Green Deal.</li><li data-v-ed4c44b9>Enhance collaboration between universities and government bodies.</li></ul></li><li class="objective-item" data-v-ed4c44b9> Transforming education programs and training specialists in sustainability <ul data-v-ed4c44b9><li data-v-ed4c44b9>Update curricula to include &quot;green skills.&quot;</li><li data-v-ed4c44b9>Develop a joint master’s program in sustainability.</li><li data-v-ed4c44b9>Introduce a “green skills” assessment system.</li><li data-v-ed4c44b9>Offer continuing education in sustainability.</li></ul></li><li class="objective-item" data-v-ed4c44b9> Expanding university-business cooperation to support ESG transformation <ul data-v-ed4c44b9><li data-v-ed4c44b9>Conduct ESG assessments and develop strategies.</li><li data-v-ed4c44b9>Organize corporate training on ESG.</li><li data-v-ed4c44b9>Encourage investment in sustainability research.</li><li data-v-ed4c44b9>Design internships for students in collaboration with businesses.</li></ul></li><li class="objective-item" data-v-ed4c44b9> Establishing the Green Deal Centre for research, innovation, and public engagement <ul data-v-ed4c44b9><li data-v-ed4c44b9>Create a platform for research and knowledge exchange.</li><li data-v-ed4c44b9>Host events on sustainability.</li><li data-v-ed4c44b9>Engage society in environmental initiatives.</li></ul></li></ol></div></section><section class="section participants" data-v-ed4c44b9><div class="responsive-container" data-v-ed4c44b9><h2 class="section-title" data-v-ed4c44b9>PARTICIPANTS</h2><ul class="participants-list" data-v-ed4c44b9><li data-v-ed4c44b9>1. CONSTRUCTOR UNIVERSITY BREMEN GGMBH, BREMEN, Germany, the Coordinator;</li><li data-v-ed4c44b9>2. SLOVENSKA POLNOHOSPODARSKA UNIVERZITA V NITRE, NITRA, Slovakia;</li><li data-v-ed4c44b9>3. EURASIAN TECHNOLOGICAL, ALMATY, Kazakhstan;</li><li data-v-ed4c44b9>4. KARAGANDA INDUSTRIAL UNIVERSITY, TEMIRTAU, Kazakhstan;</li><li data-v-ed4c44b9>5. UNIVERSITAT FUR WEITERBILDUNG KREMS, KREMS, Austria;</li><li data-v-ed4c44b9>6. BISHKEK STATE UNIVERSITY NAMED AFTER K KARASAEV, BISHKEK, Kyrgyzstan;</li><li data-v-ed4c44b9>7. YAGSHYGELDI KAKAYEV INTERNATIONAL UNIVERSITY OF OIL AND GAS, ASHGABAT, Turkmenistan;</li><li data-v-ed4c44b9>8. ABA ANNAYEW ADYNDAKY HALKARA ATCYLYK AKADEMIYASY ARKADAG, Turkmenistan;</li><li data-v-ed4c44b9>9. KHOROG STATE UNIVERSITY NAMED AFTER MYONSHO NAZARSHOEV, KHOROG, Tajikistan;</li><li data-v-ed4c44b9>10. TASHKENT KIMYO INTERNATIONAL UNIVERSITY, TASHKENT, Uzbekistan;</li><li data-v-ed4c44b9>11. TOSHKENT IRRIGASIYA VA QISHLOQ XO JALIGINI MEXANIZATSIYALASH MUHANDISLARI INSTITUTI, TASHKENT, Uzbekistan;</li><li data-v-ed4c44b9>12. UNIVERSITY OF CENTRAL ASIA NARYN, Kyrgyzstan;</li><li data-v-ed4c44b9>13. UNIVERSITY OF CENTRAL ASIA KHOROG, Tajikistan;</li></ul></div></section><section class="section expected-results" data-v-ed4c44b9><div class="responsive-container" data-v-ed4c44b9><h2 class="section-title" data-v-ed4c44b9>EXPECTED RESULTS</h2><ul class="results-list" data-v-ed4c44b9><li data-v-ed4c44b9>Active involvement of universities in the Green Deal agenda.</li><li data-v-ed4c44b9>Improved education quality with new sustainability-focused programs.</li><li data-v-ed4c44b9>Enhanced governance through ESG-driven strategies.</li><li data-v-ed4c44b9>Stronger university-business partnerships.</li><li data-v-ed4c44b9>Increased public awareness through educational campaigns.</li></ul></div></section><section class="section kpis" data-v-ed4c44b9><div class="responsive-container" data-v-ed4c44b9><h2 class="section-title" data-v-ed4c44b9>MAIN KPIs</h2><ul class="kpi-list" data-v-ed4c44b9><li data-v-ed4c44b9>10 universities with updated governance strategies.</li><li data-v-ed4c44b9>200 trained leaders in sustainability-focused management.</li><li data-v-ed4c44b9>6 educational programs updated with sustainability components.</li><li data-v-ed4c44b9>5000 students assessed for &quot;green skills.&quot;</li><li data-v-ed4c44b9>13 continuing education programs on ESG and sustainability.</li></ul></div></section>',5)])))}},r=a(l,[["__scopeId","data-v-ed4c44b9"]]);export{r as default};
